// mdv2_format_explain
const esc = s => String(s||'').replace(/([_*[\]()~`>#+\-=|{}.!\\])/g,'\\$1');

const raw = $json.text || $json.body?.text || $json.prompt || '';
const grab = (label) => {
  const re = new RegExp(String.raw`${label}\s*:\s*([\s\S]*?)(?=\n\s*\d\)\s*|$)`, 'i');
  const m = raw.match(re);
  return m ? m[1].trim() : '';
};

const s1 = grab('1\\)\\s*–ï—Å–ª–∏ –∫–æ—Ä–æ—Ç–∫–æ');
const s2 = grab('2\\)\\s*–ó–∞—á–µ–º —ç—Ç–æ –Ω—É–∂–Ω–æ');
const s3 = grab('3\\)\\s*–ß—Ç–æ —Å–¥–µ–ª–∞—Ç—å —Å–µ–π—á–∞—Å');
const s4 = grab('4\\)\\s*–ü—Ä–∏–º–µ—Ä');

let out = '';
if (s1) out += `*–ï—Å–ª–∏ –∫–æ—Ä–æ—Ç–∫–æ:* ${esc(s1)}\n\n`;
if (s2) out += `*–ó–∞—á–µ–º —ç—Ç–æ –Ω—É–∂–Ω–æ:* ${esc(s2)}\n\n`;
if (s3) out += `*–ß—Ç–æ —Å–¥–µ–ª–∞—Ç—å —Å–µ–π—á–∞—Å:* ${esc(s3)}\n\n`;
if (s4) out += `*–ü—Ä–∏–º–µ—Ä:* ${esc(s4)}\n`;

return [{
  chat_id: $json.chat_id || $json.message?.chat?.id || $json.callback_query?.message?.chat?.id,
  text: out || esc(raw)  // –Ω–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π ‚Äî –µ—Å–ª–∏ —Ä–∞—Å–ø–∞—Ä—Å–∏—Ç—å –Ω–µ —É–¥–∞–ª–æ—Å—å
}];

